export default function about() {
    return (
        <div className="pt-24 bg-gradient-to-br from-purple-100 to-purple-200 w-full h-screen">
            <div className="flex flex-col w-5/6 md:w-1/2 mx-auto space-y-4 bg-gradient-to-br from-purple-300 to-purple-500 rounded-md my-20 p-8">
                <h2 className="text-white font-bold text-4xl text-center">Welcome.</h2>
                <p className="font-medium text-lg font-serif"> <br></br>"Shopoholic"is a Professional ecommerce Platform. Here we will provide you only interesting content, which you will like very much. We're dedicated to providing you the best of ecommerce, with a focus on dependability and Shopping. We're working to turn our passion for ecommerce into a booming online website. We hope you enjoy our ecommerce as much as we enjoy offering them to you.</p>
                <h4 className="text-black-500 font-bold text-4xl text-center">Thanks For Visiting Our Site.</h4>
            </div>
        </div>
    )
}
