import React, { useContext } from "react";
import { useHistory } from "react-router-dom";
import { ModalContext, ShopContext} from "../../contexts/ShoppingContext";
import ProdCardSidebar from "../productCards/ProdCardSidebar";


function SideBar() {
    const {cartProducts, setCartProducts} = useContext(ShopContext);
    const { cartVisible, setCartVisible } = useContext(ModalContext);
    const history = useHistory();

    return (
        <React.Fragment>
            <div onClick={() => setCartVisible(false)} className={`fixed inset-0 bg-neon1-light-500 h-full w-full z-20 overflow-y-auto transform ease-linear duration-200 transition-opacity ${cartVisible ? "bg-opacity-50 translate-x-0" : "bg-opacity-0 translate-x-full"}`}></div>
            <div className={`transition-all transform ease-in-out duration-300 flex flex-col h-full bg-gradient-to-br from-neon1-light-1000 to-purple-200 fixed right-0 top-0 z-20 w-9/12 sm:w-7/12 md:w-6/12 lg:w-2/5 xl:w-1/3 2xl:w-1/4 ${cartVisible ? "translate-x-0" : "translate-x-full"}`}>
                <div className="bg-gradient-to-tr from-green-100 to-green-300 px-2 py-4 items-center flex">
                    <h6 className="text-black-500 font-bold text-xl sm:text-2xl lg:text-3xl flex-grow  px-2 py-1 mx-2 rounded-md">Cart</h6>
                    <div className="transition transform hover:scale-105 hover:bg-opacity-75 cursor-pointer  px-3 py-1 mx-2 rounded-xl text-dark-100 text-2xl" onClick={() => { setCartVisible(false) }}>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg></div>
                </div>
                <div className="overflow-y-auto flex-grow">
                    {
                        Object.values(cartProducts).map((product) => <ProdCardSidebar product={product} />)
                    }
                </div>

                <div className="flex flex-col space-y-1 py-2">
                    <button className="transition transform hover:scale-102 hover:bg-opacity-80 font-semibold text-lg sm:text-lg bg-purple-100 mx-2 rounded-lg py-3" onClick={() => { setCartVisible(false); Object.keys(cartProducts).length>0 ? history.push('/checkout/') : history.push('/') }}>Proceed To Checkout</button>
                    <div className="flex py-1 px-4 bg-gray-300 rounded-md mx-2">
                        <h4 className="font-semibold text-base sm:text-lg">Total Cost: </h4>
                        <h6 className="flex-grow text-right w-1/2 text-base sm:text-lg font-bold">{Object.values(cartProducts).reduce((totalCost, product) => totalCost + (product.count * product.price), 0).toFixed(2)}<i className="fa fa-dollar text-base pl-2"></i></h6>
                    </div>
                    <button className="transition transform hover:scale-102 hover:bg-opacity-90 font-medium text-base sm:text-lg bg-purple-500 mx-2 rounded-lg py-1" onClick={e => setCartProducts({})}>Remove All</button>
                </div>
            </div>
        </React.Fragment>
    );
}

export default SideBar;
